bsp;&nbsp;&nbsp;else</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;printf(\"%5s%c\",\" \",2);</p><p>&nbsp;&nbsp;&nbsp;}</p><p>&nbsp;&nbsp;&nbsp;return 0;</p><p>}</p><p>int print6(Point p)</p><p>{</p><p>&nbsp;&nbsp;&nbsp;int i=0;</p><p>&nbsp;&nbsp;&nbsp;for(;i&lt;13;i++)</p><p>&nbsp;&nbsp;&nbsp;{</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;gotoxy(p.x+1, p.y+i);</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(i==0||i==6||i==12)</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;printf(\"%c%c%c%c%c%c\",2,2,2,2,2,2);</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else if(i&gt;0&amp;&amp;i&lt;6)</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;printf(\"%c\",2);</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;printf(\"%c%4s%c\",2,\" \",2);</p><p>&nbsp;&nbsp;&nbsp;}</p><p>&nbsp;&nbsp;&nbsp;return 0;</p><p>}</p><p>int print7(Point p)</p><p>{</p><p>&nbsp;&nbsp;&nbsp;int i=0;</p><p>&nbsp;&nbsp;&nbsp;for(;i&lt;13;i++)</p><p>&nbsp;&nbsp;&nbsp;{</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;gotoxy(p.x+1, p.y+i);</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(i==0)&nbsp;&nbsp;printf(\"%c%c%c%c%c%c\",2,2,2,2,2,2);</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else&nbsp;&nbsp;&nbsp;&nbsp;printf(\"%5s%c\",\" \",2);</p><p>&nbsp;&nbsp;&nbsp;}</p><p>&nbsp;&nbsp;&nbsp;return 0;</p><p>}</p><p>int print8(Point p)</p><p>{</p><p>&nbsp;&nbsp;&nbsp;int i=0;</p><p>&nbsp;&nbsp;&nbsp;for(;i&lt;13;i++)</p><p>&nbsp;&nbsp;&nbsp;{</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;gotoxy(p.x+1, p.y+i);</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(i==0||i==6||i==12)</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;printf(\"%c%c%c%c%c%c\",2,2,2,2,2,2);</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else&nbsp;printf(\"%c%4s%c\",2,\" \",2);</p><p>&nbsp;&nbsp;&nbsp;}</p><p>&nbsp;&nbsp;&nbsp;return 0;</p><p>}</p><p>int print9(Point p)</p><p>{</p><p>&nbsp;&nbsp;&nbsp;int i=0;</p><p>&nbsp;&nbsp;&nbsp;for(;i&lt;13;i++)</p><p>&nbsp;&nbsp;&nbsp;{</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;gotoxy(p.x+1, p.y+i);</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(i==0||i==6||i==12)</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;printf(\"%c%c%c%c%c%c\",2,2,2,2,2,2);</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else if(i&gt;0&amp;&amp;i&lt;6)</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;printf(\"%c%4s%c\",2,\" \",2);</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;printf(\"%5s%c\",\" \",2);</p><p>&nbsp;&nbsp;&nbsp;}</p><p>&nbsp;&nbsp;&nbsp;return 0;</p><p>}</p><p>int clear(Point p)</p><p>{</p><p>&nbsp;&nbsp;&nbsp;int i=0;</p><p>&nbsp;&nbsp;&nbsp;for(;i&lt;13;i++)</p><p>&nbsp;&nbsp;&nbsp;gotoxy(p.x,p.y+i); printf(\"%16s\",\" \");</p><p>&nbsp;&nbsp;&nbsp;return 0;</p><p>}</p><p>int printtime(Point p, int n)</p><p>{</p><p>&nbsp;&nbsp;&nbsp;int a,b;</p><p>&nbsp;&nbsp;&nbsp;Point pp;</p><p>&nbsp;&nbsp;&nbsp;a=n/10, b=n%10;</p><p>&nbsp;&nbsp;&nbsp;pp.x=p.x+8, pp.y=p.y;</p><p>&nbsp;&nbsp;&nbsp;switch(a)</p><p>&nbsp;&nbsp;&nbsp;{</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case 0: print0(p); break;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case 1: print1(p); break;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case 2: print2(p); break;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case 3: print3(p); break;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case 4: print4(p); break;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case 5: print5(p); break;</p><p>&nbsp;&nbsp;}</p><p>&nbsp;&nbsp;switch(b)</p><p>&nbsp;&nbsp;{</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case 0: print0(pp); break;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case 1: print1(pp); break;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case 2: print2(pp); break;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case 3: print3(pp); break;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case 4: print4(pp); break;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case 5: print5(pp); break;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case 6: print6(pp); break;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case 7: print7(pp); break;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case 8: print8(pp); break;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;case 9: print9(pp); break;</p><p>&nbsp;&nbsp;}</p><p>&nbsp;&nbsp;return 0;</p><p>}</p><p>int main()</p><p>{</p><p>&nbsp;&nbsp;&nbsp;Point phour, pmin, psec,point1,point2;</p><p>&nbsp;&nbsp;&nbsp;phour.x=9, pmin.x=32, psec.x=55;</p><p>&nbsp;&nbsp;&nbsp;phour.y=pmin.y=psec.y=7;</p><p>&nbsp;&nbsp;&nbsp;point1.x=25, point2.x=49;</p><p>&nbsp;&nbsp;&nbsp;point1.y=point2.y=7;</p><p>&nbsp;&nbsp;&nbsp;clrscr();</p><p>&nbsp;&nbsp;&nbsp;textbackground(YELLOW);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/* 设置背景颜色*/</p><p>&nbsp;&nbsp;&nbsp;textcolor(RED);&nbsp;&nbsp;&nbsp;/* 设置数字显示颜色*/</p><p>&nbsp;&nbsp;&nbsp;now=time(0);</p><p>&nbsp;&nbsp;&nbsp;pt=localtime(&amp;now);</p><p>&nbsp;&nbsp;&nbsp;t1=*pt;</p><p>&nbsp;&nbsp;&nbsp;printtime(phour, t1.tm_hour);</p><p>&nbsp;&nbsp;&nbsp;printpoint(point1);</p><p>&nbsp;&nbsp;&nbsp;printtime(pmin, t1.tm_min);</p><p>&nbsp;&nbsp;&nbsp;printpoint(point2);</p><p>&nbsp;&nbsp;&nbsp;printtime(psec, t1.tm_sec);</p><p>&nbsp;&nbsp;&nbsp;while(1)</p><p>&nbsp;&nbsp;&nbsp;{</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;now=time(0);</p><p>&nbsp;&nbsp;&nbsp;&nbsp;pt=localtime(&amp;now);</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;t2=*pt;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(t2.tm_sec!=t1.tm_sec)</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;t1=t2;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;clrscr();</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;printtime(phour, t1.tm_hour);</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;printpoint(point1);</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;printtime(pmin, t1.tm_min);</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;printpoint(point2);</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;printtime(psec, t1.tm_sec);</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(bioskey(1)==0) continue;</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;else exit(0);</p><p>&nbsp;&nbsp;&nbsp;}</p><p>&nbsp;&nbsp;&nbsp;return 0;</p><p>}</p>', '2023-04-21', '程志烨');
INSERT INTO blog VALUES ('9', 'C语言练习', '<p>例11.6 有学生成绩数据文件score.dat文件，其格式为：</p><p><br></p><p><br></p><p><br></p><p>李小明 70</p><p><br></p><p>赵伟 65</p><p><br></p><p>刘春丽 90.5</p><p><br></p><p>……</p><p><br></p><p>请计算出平均分，并将结果写入到最后一行。形如：</p><p><br></p><p><br></p><p><br></p><p>平均分 86.3</p><p><br></p><p><br></p><p><br></p><p>#include &lt;stdio.h&gt;</p><p><br></p><p>main()</p><p><br></p><p>{ FILE *fp;</p><p><br></p><p>char name[20];</p><p><br></p><p>float score,sum=0;</p><p><br></p><p>int n=0;</p><p><br></p><p>fp=fopen(\"score.dat\",\"r+\");</p><p><br></p><p>while(fscanf(fp,\"%s%f\",name,&amp;score)==2)</p><p><br></p><p>{ sum+=score;</p><p><br></p><p>n++;</p><p><br></p><p>}</p><p><br></p><p>fprintf(fp,\"平均分\\t%5.1f\\n\",sum/n);</p><p><br></p><p>fclose(fp);</p><p><br></p><p>}</p><p><br></p><p><br></p><p>例11.7 将给定的学生数据保存到“学生.dat”二进制文件中，然后将这些数据读出并显示在屏幕上。</p><p><br></p><p><br></p><p><br></p><p>#include &lt;stdio.h&gt;</p><p><br></p><p><br></p><p><br></p><p>struct STUDENT</p><p><br></p><p>{ int ID;</p><p><br></p><p>char name[10];</p><p><br></p><p>int age;</p><p><br></p><p>char addr[15];</p><p><br></p><p>};</p><p><br></p><p><br></p><p><br></p><p>int SaveToFile(struct STUDENT s[],int n)</p><p><br></p><p>{ int i;</p><p><br></p><p>FILE *f;</p><p><br></p><p>if((f=fopen(\"d:\\\\学生.dat\",\"wb\"))==NULL)</p><p><br></p><p>{</p><p><br></p><p>printf(\"学生.dat文件不能创建。\\n\");</p><p><br></p><p>return 0;</p><p><br></p><p>}</p><p><br></p><p>for(i=0;i&lt;n;i++)</p><p><br></p><p>if(fwrite(&amp;s[i],sizeof(struct STUDENT),1,f)!=1)</p><p><br></p><p>{</p><p><br></p><p>printf(\"在写入第%d名学生数据时发生错误，文件建立不完整。\\n\",i+1);</p><p><br></p><p>return i;</p><p><br></p><p>}</p><p><br></p><p>fclose(f);</p><p><br></p><p>return i;</p><p><br></p><p>}</p><p><br></p><p><br></p><p><br></p><p>int LoadAndDisplay()</p><p><br></p><p>{ struct STUDENT s;</p><p><br></p><p>int n=0;</p><p><br></p><p>FILE *f;</p><p><br></p><p>if((f=fopen(\"d:\\\\学生.dat\",\"rb\"))==NULL)</p><p><br></p><p>{</p><p><br></p><p>printf(\"学生.dat文件不能使用。\\n\");</p><p><br></p><p>return 0;</p><p><br></p><p>}</p><p><br></p><p>printf(\"学号\\t姓名\\t年龄\\t地址\\n\");</p><p><br></p><p>while(fread(&amp;s,sizeof(struct STUDENT),1,f)==1)</p><p><br></p><p>{</p><p><br></p><p>printf(\"%d\\t%s\\t%d\\t%s\\n\",s.ID,s.name,s.age,s.addr);</p><p><br></p><p>n++;</p><p><br></p><p>}</p><p><br></p><p>fclose(f);</p><p><br></p><p>return n;</p><p><br></p><p>}</p><p><br></p><p><br></p><p><br></p><p>main()</p><p><br></p><p>{</p><p><br></p><p>struct STUDENT students[]=</p><p><br></p><p>{{1001,\"李明\",18,\"北京\"},</p><p><br></p><p>{1002,\"王丽\",19,\"上海\"},</p><p><br></p><p>{1003,\"赵新欣\",18,\"天津\"},</p><p><br></p><p>{1004,\"陈浩然\",20,\"沈阳\"}};</p><p><br></p><p><br></p><p><br></p><p>int stu_cnt=4,save_n,disp_n;</p><p><br></p><p><br></p><p><br></p><p>save_n=SaveToFile(students,stu_cnt);</p><p><br></p><p>if(save_n&gt;0)</p><p><br></p><p>{</p><p><br></p><p>disp_n=LoadAndDisplay();</p><p><br></p><p>if(disp_n&gt;0)</p><p><br></p><p>printf(\"\\n共读入并显示了%d名学生的信息。\\n\",disp_n);</p><p><br></p><p>}</p><p><br></p><p>}</p><p><br></p><p><br></p><p>指针示例</p><p><br></p><p>#include &lt;stdio.h&gt;</p><p><br></p><p><br></p><p><br></p><p>main()</p><p><br></p><p>{</p><p><br></p><p>&nbsp;int a[]={10,20,30,40,50};</p><p><br></p><p>&nbsp;int *p,x;</p><p><br></p><p>&nbsp;p=&amp;a[0];</p><p><br></p><p>&nbsp;printf(\"\\n\\n%d \",*p);</p><p><br></p><p>&nbsp;printf(\"%d \",*p++);</p><p><br></p><p>&nbsp;printf(\"%d \",(*p)++);</p><p><br></p><p>&nbsp;printf(\"%d \",*(p++));</p><p><br></p><p>&nbsp;printf(\"%d \",++*p);</p><p><br></p><p>&nbsp;printf(\"%d \",++(*p));</p><p><br></p><p>&nbsp;printf(\"%d \",*++p);</p><p><br></p><p>&nbsp;printf(\"%d \",*(++p));</p><p>}</p>', '2023-04-21', '程志烨');
